import React, { useState } from 'react';

import Card from '../UI/Card';
import Button from '../UI/Button';
import classes from './AddUser.module.css';

const AddUser = (props) => {
  const [username, setUsername] = useState('');
  const [age, setAge] = useState('');

  const addUserHandler = (event) => {    
    event.preventDefault();
    console.log("Username: " + username + "  Age: " + age);
    setUsername('');
    setAge('');
    console.log("After submit Username: " + username + "  Age: " + age);
  };

  const changeUsernameHandler = (event) => {
    if(event.target.value.length > 0)
        setUsername(event.target.value);
  }

  const changeAgeHandler = (event) => {
    if(event.target.value > 0)
        setAge(event.target.value);
  }

  return (

    <Card className={classes.input}>
      <form onSubmit={addUserHandler}>
        <label htmlFor="username">Username</label>
        <input id="username" type="text" placeholder='Enter username' onChange={changeUsernameHandler}/>
        <label htmlFor="age">Age (Years)</label>
        <input id="age" type="number" placeholder='Enter user age' onChange={changeAgeHandler}/>
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};

export default AddUser;
